import mysql.connector; conn = mysql.connector.connect(host='localhost', user='root', password='password'); cursor = conn.cursor(); cursor.execute('CREATE DATABASE IF NOT EXISTS investment_db'); cursor.execute('USE investment_db'); cursor.execute('CREATE TABLE IF NOT EXISTS investment (investment_id VARCHAR(36) PRIMARY KEY, investment_amount DECIMAL(15, 2) NOT NULL, investment_date DATE NOT NULL, annual_return_percentage DECIMAL(5, 2) NOT NULL, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, INDEX idx_investment_date (investment_date))'); conn.commit(); cursor.close(); conn.close(); print('âœ… Database and table created successfully!')